#ifndef HTML_MAIN
#define HTML_MAIN

const char HTML_INDEX[] = "<!DOCTYPE html>\n<html lang=\"en\">\n    <head>\n        <meta charset=\"UTF-8\" />\n        <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\" />\n        <script src=\"https://unpkg.com/htmx.org@1.9.10\"></script>\n        <title>Public LED Website on an Arduino</title>\n        <style>\n            body {\n                text-align: center;\n                margin-left: auto;\n                margin-right: auto;\n            }\n\n            .ledstatus div {\n                display: inline-block;\n                padding: 1vw;\n                font-weight: bold;\n                border-radius: 25px;\n                width: 10vw;\n                text-align: center;\n            }\n            .ledstatus {\n                display: inline-block;\n                margin: 5%;\n                cursor: pointer;\n            }\n            .ledstatus h1 {\n                margin: 0;\n                font-size: 18pt;\n            }\n\n            /* LED statuses */\n            .ledstatus .load {\n                background-color: rgb(255, 255, 0);\n            }\n            .ledstatus .on {\n                background-color: rgb(34, 222, 34);\n            }\n            .ledstatus .off {\n                background-color: rgb(226, 9, 9);\n            }\n\n        </style>\n    </head>\n    <body>\n        <h1>Public LED website on an Arduino</h1>\n        <p>Click to turn on a LED!!!!</p>\n        <div id=\"led-status\">\n            <div class=\"ledstatus\" id=\"led1\"  hx-trigger=\"every 1s\" hx-get=\"/led/1\">\n                <h1>LED #1</h1>\n                <div class=\"load\">LOAD</div>\n            </div>\n            <div class=\"ledstatus\" id=\"led2\" hx-trigger=\"every 1s\" hx-get=\"/led/2\">\n                <h1>LED #2</h1>\n                <div class=\"load\">LOAD</div>\n            </div>\n            <div class=\"ledstatus\" id=\"led3\" hx-trigger=\"every 1s\" hx-get=\"/led/3\">\n                <h1>LED #3</h1>\n                <div class=\"load\">LOAD</div>\n            </div>\n        </div>\n    </body>\n</html>";

#endif // HTML_MAIN
